import React, { useState } from 'react';

const SearchBar = ({ onClick }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleInputChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
  };

  const handleSearch = () => {
    const searchQuery = searchTerm.trim().toLowerCase();
    if (searchQuery) {
      // Make your API request here with the appropriate URL and query parameters
      fetch(`http://localhost:9292/exercises?search=${searchQuery}`)
        .then(response => response.json())
        .then(data => {
          const filteredExercises = data.results; // Adjust this based on your API response
          onClick(filteredExercises); // Pass the filtered exercises to the parent component
        });
    }
  };

  return (
    <div class="search">
      <div class="search-box">
        <div class="search-field">
          <input
            placeholder="Search..."
            className="input"
            type="text"
            value={searchTerm}
            onChange={handleInputChange}
          />
          <div class="search-box-icon">
            <button class="btn-icon-content" onClick={handleSearch}>
              <i class="search-icon">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  version="1.1"
                  viewBox="0 0 512 512"
                >
                  <path
                    d="M416 208c0 45.9-14.9 88.3-40 122.7L502.6 457.4c12.5 12.5 12.5 32.8 0 45.3s-32.8 12.5-45.3 0L330.7 376c-34.4 25.2-76.8 40-122.7 40C93.1 416 0 322.9 0 208S93.1 0 208 0S416 93.1 416 208zM208 352a144 144 0 1 0 0-288 144 144 0 1 0 0 288z"
                    fill="#fff"
                  ></path>
                </svg>
              </i>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SearchBar;
