import React, { useState, useEffect } from 'react';

import magicMouse from 'magicmouse.js';

const MagicMouseComponent = () => {
  const [isMagicMouseEnabled, setIsMagicMouseEnabled] = useState(true);

  useEffect(() => {
    if (isMagicMouseEnabled) {
      magicMouse();
    }
  }, [isMagicMouseEnabled]);

  const toggleMagicMouse = () => {
    setIsMagicMouseEnabled(!isMagicMouseEnabled);
  };

  return (
    <div>
      <button onClick={toggleMagicMouse}>
        {isMagicMouseEnabled ? 'Disable Magic Mouse' : 'Enable Magic Mouse'}
      </button>
      <button className="magic-hover magic-hover__square">
        Hover Me
      </button>
    </div>
  );
};

export default MagicMouseComponent;
