import React, { useState, useEffect } from 'react';

const WorkoutPlanner = () => {
  const [exercises, setExercises] = useState([]);
  const [selectedExercise, setSelectedExercise] = useState('');
  const [workoutExercises, setWorkoutExercises] = useState([]);

  useEffect(() => {
    // Fetch exercises from the backend
    fetch('http://localhost:9292/exercises')
      .then((response) => response.json())
      .then((data) => setExercises(data))
      .catch((error) => console.log(error));
  }, []);

  const handleExerciseChange = (e) => {
    setSelectedExercise(e.target.value);
  };

  const handleAddExercise = () => {
    const exerciseToAdd = exercises.find((exercise) => exercise.id === selectedExercise);
    if (exerciseToAdd) {
      const updatedExercise = {
        ...exerciseToAdd,
        sets: 0,
        reps: 0,
        duration: 0
      };
      setWorkoutExercises([...workoutExercises, updatedExercise]);
    }
  };

  const handleSetsChange = (index, e) => {
    const updatedExercises = [...workoutExercises];
    updatedExercises[index].sets = parseInt(e.target.value);
    setWorkoutExercises(updatedExercises);
  };

  const handleRepsChange = (index, e) => {
    const updatedExercises = [...workoutExercises];
    updatedExercises[index].reps = parseInt(e.target.value);
    setWorkoutExercises(updatedExercises);
  };

  const handleDurationChange = (index, e) => {
    const updatedExercises = [...workoutExercises];
    updatedExercises[index].duration = parseInt(e.target.value);
    setWorkoutExercises(updatedExercises);
  };

  return (
    <div>
      <h2>Workout Planner</h2>
      <div>
        <label>Select Exercise:</label>
        <select value={selectedExercise} onChange={handleExerciseChange}>
          <option value="">Select an exercise</option>
          {exercises.map((exercise) => (
            <option key={exercise.id} value={exercise.id}>
              {exercise.name}
            </option>
          ))}
        </select>
        <button onClick={handleAddExercise}>Add Exercise</button>
      </div>
      <h3>Workout Exercises:</h3>
      <ul>
        {workoutExercises.map((exercise, index) => (
          <li key={exercise.id}>
            {exercise.name} - Sets:
            <input type="number" value={exercise.sets} onChange={(e) => handleSetsChange(index, e)} />
            Reps:
            <input type="number" value={exercise.reps} onChange={(e) => handleRepsChange(index, e)} />
            Duration:
            <input type="number" value={exercise.duration} onChange={(e) => handleDurationChange(index, e)} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WorkoutPlanner;
