import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Login = () => {
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const [username, setUsername] = useState('');

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = (e) => {
    e.preventDefault();

    // Prepare the login data
    const loginData = {
      username,
      password,
    };

    // Send the login request to the API
    fetch('/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(loginData), // Pass the loginData variable instead of just {username}
    })
      .then((response) => {
        if (response.ok) {
          console.log('Login successful');
          setUsername('');
          setPassword('');
          navigate('/home');
        } else {
          console.log('Login failed');
        }
      })
      .catch((error) => {
        console.log('Login error:', error);
      });
  };

  return (
    <div className="login-container">
      <div className="login-content">
        <div className="login-box">
          <p>Login</p>
          <form onSubmit={handleLogin}>
            <div className="user-box">
              <label>Username:</label> {/* Add label text */}
              <input type="text" value={username} onChange={handleUsernameChange} placeholder="Username" />
            </div>
            <div className="user-box">
              <label>Password:</label> {/* Add label text */}
              <input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
            </div>
            <button type="submit">Login</button> {/* Replace anchor tag with button */}
          </form>
          <p>
            Don't have an account?
            <Link to="/register" className="a2">
              Sign up!
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
